import React, { useState } from "react";
import emailjs from "emailjs-com";
import './contact.styles.scss';

const Contact = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");
  const [emailSent, setEmailSent] = useState(false);
  const submit = () => {
    if (name && email && message) {
      // TODO - send mail
      const serviceId = "service_lhsobpo";
      const templateId = "template_n91z8yj";
      const userId = "BvX3RwNBsra42lP-e";
      const templateParams = {
        name,
        email,
        message,
      };

      emailjs
        .send(serviceId, templateId, templateParams, userId)
        .then((response) => console.log(response))
        .then((error) => console.log(error));
      setName("");
      setEmail("");
      setMessage("");
      setEmailSent(true);
    } else {
      alert("Please fill in all fields.");
    }
  };

  const isValidEmail = (email) => {
    const regex =
      /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    return regex.test(String(email).toLowerCase());
  };

  return (
    <div>
      <div className="contact-main" style = {{display:'flex', flexDirection:'column'}}>
      <h1 style={{fontFamily: "Playball, cursive"}}>Contact Me</h1>
        <input
          className="contactField"
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          className="contactField"
          type="email"
          placeholder="Your email address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <textarea
          className="contactField"
          placeholder="Your message"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        ></textarea>
        <button className="contactField" onClick={submit}>
          Send Message
        </button>
        <span className={emailSent ? "visible" : null}>
          Thank you for your message, we will be in touch in no time!
        </span>
      </div>
    </div>
  );
};

export default Contact;
